import { useContext } from "react";
import { DataContext } from "../App";
import QuestionData from "../data/QuestionData";

const Score =()=>{
    const {score} = useContext(DataContext)
    return(
        <div className="score">
            <h1>สรุปผลคะแนน</h1>
            <h2>{score}/{QuestionData.length}</h2>
        </div>
    )
}
export default Score;
